import React, { useState } from 'react';
import { IoSearchSharp } from "react-icons/io5";
import './Sidebar.css'; // If you have custom styles
import OtherUsers from './OtherUsers';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { setOtherUsers } from '../redux/userSlice';

const Sidebar = () => {
  const [search, setSearch] = useState("");
  const otherUsers = useSelector(store => store.user.otherUsers);
  const dispatch = useDispatch();

  const navigate = useNavigate();

  const searchSubmitHandler = async (e) => {
    e.preventDefault();
    const conversationUser = otherUsers?.find(user => user.fullName.toLowerCase().includes(search.toLowerCase()));
    if (conversationUser) {
      dispatch(setOtherUsers([conversationUser]));
    } else {
      toast.error("User not found");
    }
  };

  const logoutHandler = async () => {
    try {
      const response = await axios.get("http://localhost:8002/api/logout");
      toast.success(response.data.message);
      navigate("/login");
    } catch (error) {
      console.log("Error in logout component", error);
    }
  };

  return (
    <div className="sidebar bg-gray-800 text-white p-4">
      <form onSubmit={searchSubmitHandler} className="flex mb-4">
        <input
          className="input w-full p-2 rounded-l-lg bg-gray-700 border-none"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          type="text"
          placeholder="Search..."
        />
        <button type="submit" className="btn p-2 rounded-r-lg bg-blue-500 hover:bg-blue-600">
          <IoSearchSharp />
        </button>
      </form>
      <div className="divider border-t border-gray-600 my-4"></div>
      <OtherUsers />
      <div className="mt-2">
        <button onClick={logoutHandler} className="btn btn-sm bg-red-500 hover:bg-red-600 p-2 rounded">
          Logout
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
