import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMessages } from "../redux/messageSlice";
import { useSocket } from '../Contexts/SocketContext';

const useGetRealTimeMessage = () => {
  const socket = useSocket();
  const { messages } = useSelector(store => store.message);
  const { authUser, selectedUser } = useSelector(store => store.user);
  const dispatch = useDispatch();

  useEffect(() => {
    if (socket && authUser) {
      socket.on("newMessage", (newMessage) => {
        // Only add the message if it's intended for the current user or sent by the current user to the selected user
        if (
          (newMessage.receiverId === authUser._id && newMessage.senderId === selectedUser?._id) ||
          (newMessage.senderId === authUser._id && newMessage.receiverId === selectedUser?._id)
        ) {
          dispatch(setMessages([...messages, newMessage]));
        }
      });
    }
  }, [socket, messages, authUser, selectedUser, dispatch]);
};

export default useGetRealTimeMessage;
