import './App.css';
import './index.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Signup from './Components/Signup';
import Login from './Components/Login';
import Home from './Components/Home';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { io } from "socket.io-client";
import {setSocket} from './redux/socketSlice'
import { setOnlineUsers } from './redux/userSlice';

function App() {
  //const [socket, setSocket] = useState(null);
  const { authUser } = useSelector(store => store.user);
  const { socket } = useSelector(store => store.socket);
  const dispatch = useDispatch();
  console.log("authUser",authUser)

  useEffect(() => {
    if (authUser) {
      // Initialize Socket.IO connection
      const socketInstance = io('http://localhost:8005', {
        query: {
          userId: authUser._id
        },
        // transports: ['websocket'], // Optional, uncomment if needed
        // withCredentials: true // Optional, uncomment if needed
      });

      setSocket(socketInstance);

      // Listen for online users event
      socketInstance.on('getOnlineUsers', (onlineUsers) => {
        dispatch(setOnlineUsers(onlineUsers));
        console.log("onlineUsers",onlineUsers)
      });

      // Handle socket connection
      socketInstance.on('connect', () => {
        console.log('Connected to server');
      });

      // Cleanup on component unmount
      return () => {
        socketInstance.disconnect();
      };
    }else{
      if(socket){
        socket.close()
        dispatch(setSocket(null))
      }
    }
  }, [authUser, dispatch]);

  return (
    <BrowserRouter>
      <div className='p-4 h-screen flex items-center justify-center'>
        <img src="/bg4.jpeg" alt="Background" className="background-image" />
        <Routes>
          <Route path="/" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/home" element={<Home />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
