import React, { useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';

const Message = ({ message }) => {
  const scroll = useRef();
  const { authUser, selectedUser } = useSelector(store => store.user);

  useEffect(() => {
    scroll.current?.scrollIntoView({ behavior: "smooth" });
  }, [message]);

  const isSenderAuthUser = authUser?._id === message?.senderId;
  const profilePic = isSenderAuthUser ? authUser?.profilePhoto : selectedUser?.profilePhoto;
  const formattedTime = new Date(message?.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  return (
    <div ref={scroll} className={`chat ${isSenderAuthUser ? 'chat-end' : 'chat-start'}`}>
      <div className="chat-image avatar">
        <div className="w-10 rounded-full">
          <img alt="Profile" src={profilePic} />
        </div>
      </div>
      <div className="chat-header">
        <time className="text-xs opacity-50">{formattedTime}</time>
      </div>
      <div className={`chat-bubble ${isSenderAuthUser ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'}`}>
        {message?.message}
      </div>
    </div>
  );
}

export default Message;
