import React, { useState } from 'react';
import { IoMdSend } from "react-icons/io";
import './SendInput.css';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { setMessages } from '../redux/messageSlice';
import { useSocket } from '../Contexts/SocketContext'; // Ensure you have this context

const SendInput = () => {
  const [message, setMessage] = useState("");
  const dispatch = useDispatch();
  const { selectedUser } = useSelector(store => store.user);
  const { authUser } = useSelector(store => store.user); // Assuming authUser is stored in user slice
  const { messages } = useSelector(store => store.message);
  const socket = useSocket(); // Access the socket instance

  const onSubmitHandler = async (e) => {
    e.preventDefault();
    if (!selectedUser?._id) {
      console.log("No user selected");
      return;
    }

    const newMessage = {
      senderId: authUser._id,
      receiverId: selectedUser._id,
      content: message,
      timestamp: new Date(),
    };

    // Send message through socket
    socket.emit("sendMessage", newMessage);

    // Optionally save the message to the backend
    try {
      const response = await axios.post(
        `http://localhost:8002/message/sendMessage/${selectedUser._id}`,
        { message },
        {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true,
        }
      );
      console.log("Response after sending message", response);
      dispatch(setMessages([...messages, response?.data?.newMessage]));
    } catch (error) {
      console.log("Error in SendInput component", error);
    }
    setMessage("");
  };

  return (
    <form onSubmit={onSubmitHandler} className='send-input-form'>
      <div className='input-container'>
        <input
          type='text'
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder='Send a message...'
          className='input-field'
        />
        <button type="submit" className='send-button'>
          <IoMdSend />
        </button>
      </div>
    </form>
  );
};

export default SendInput;
