import React, { useEffect } from 'react';
import './Messages.css';
import Message from './Message';
import useGetMessages from '../Hooks/useGetMessages'; // Ensure this is used if needed to fetch initial messages
import useGetRealTimeMessage from '../Hooks/useRealTimeMessages'; // Ensure this import path is correct
import { useSelector } from 'react-redux';

const Messages = () => {
  // Fetch initial messages if needed
  useGetMessages();
  // Setup real-time message listener
  useGetRealTimeMessage();

  const { messages } = useSelector(store => store.message);

  if (!messages || messages.length === 0) {
    return <div className='messages-container'>No messages yet.</div>;
  }

  return (
    <div className='messages-container'>
      {messages.map((message) => (
        <Message key={message._id} message={message} />
      ))}
    </div>
  );
};

export default Messages;
