import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setSelectedUser } from '../redux/userSlice';
import './OtherUser.css'; // Ensure to import the CSS file

const OtherUser = ({ user }) => {
  const dispatch = useDispatch();
  const { selectedUser, onlineUsers = [] } = useSelector(store => store.user); // Default to empty array if null

  const isOnline = onlineUsers.includes(user._id);

  const selectedUserHandler = () => {
    console.log("user is", user);
    dispatch(setSelectedUser(user));
  };

  return (
    <div onClick={selectedUserHandler} className={`user ${selectedUser?._id === user?._id ? 'selected' : ''}`}>
      <img src={user.profilePic} alt={user.fullName} />
      <div className="user-info">
        <div className="user-name">{user.fullName}</div>
        <div className={`avatar ${isOnline ? 'online' : ''}`}></div>
      </div>
    </div>
  );
};

export default OtherUser;
