import React, { useState } from 'react';
import {Link,useNavigate} from 'react-router-dom'
import axios from 'axios'
import toast from 'react-hot-toast'


const Login = () => {
    const[user,setUser]=useState({
        username:"",
        Password:"",
      })
      const navigate=useNavigate()
      const handleSubmit=async(event)=>{
        event.preventDefault();
        try {
            const res = await axios.post("http://localhost:8002/api/signup", user, {
              headers: {
                "Content-Type": "application/json"
              },
              withCredentials: true
            });
            console.log("Response is ", res);
            if(res.data.success){
              navigate("/login")
              toast.success(res.data.message);
            }
          } catch (error) {
            console.log("Error in signup component", error);
          }
        console.log("User is",user)
        setUser({
        username:"",
        Password:"",
        })
      }
  return (
    <div className='w-full max-w-md p-6 rounded-lg shadow-md bg-gray-800 bg-opacity-75'>
      <h1 className='text-3xl font-bold text-center text-white'>Login</h1>
      <form onSubmit={handleSubmit} action="">
        
        <div className='mb-4'>
          <label className='block text-white'>
            <span className='text-base'>Username</span>
            <input
              className='mt-1 w-full p-2 rounded-md bg-gray-200'
              value={user.username}
              onChange={(e)=>{setUser({...user,username:e.target.value})}}
              type='text'
              placeholder='Username'
            />
          </label>
        </div>
        <div className='mb-4'>
          <label className='block text-white'>
            <span className='text-base'>Password</span>
            <input
              className='mt-1 w-full p-2 rounded-md bg-gray-200'
              type='password'
              placeholder='Password'
              value={user.Password}
              onChange={(e)=>{setUser({...user,Password:e.target.value})}}
            />
          </label>
        </div>
        <Link to="/">
        Do not have an account
        </Link>
        
        <div>
          <button type="submit" className='btn btn-block btn-sm mt-2 border border-slate-700'>Login</button>
        </div>
      </form>
    </div>
  )
}

export default Login
